import React, { useState } from "react";
import styled from "styled-components";
import Sidebar from "../Components/Sidebar";

//명함 만들기 main 화면
const MakemainPositioner = styled.div`
  display: flex;
`;
// 출력폼
const Outputform = styled.div`
  border-style: solid;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
`;

// 입력폼
const Inputform = styled.div`
  border-style: solid;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
`;
// 명함 컬러 선택하기
const Cololselector = styled.div`
  display: flex;
  justify-content: center;
  font-size: 20px;
  flex-wrap: wrap;
`;

const Colorbuttons = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
`;

// 기본 정보 입력하기
const Basicinfo = styled.div`
  display: flex;
  height: 40px;
  justify-content: center;
  font-size: 20px;
  margin-top: 1rem;
  flex-wrap: wrap;
`;

const Basicinfoinputs = styled.div`
  display: flex;
  align-items: flex-end;
  flex-direction: column;
`;

const Basicinfoinputposition = styled.div``;

const Infoinput = styled.input`
  width: 500px;
  font-size: 15px;
  margin-left: 3rem;
  margin-right: 8rem;
  border-radius: 15px;
`;

function Myspace({ usertoken }) {
  const { token } = usertoken || {}; // App.js에서 token값 가져오기

  const [values, setValues] = useState({
    token: token,
    color: "",
    name: "",
    mail: "",
  });

  const handleChange = (e) => {
    // Basicinfo input eventhandle function
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleClick = (e) => {
    // Colorselect button eventhandle function
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  console.log(values);

  return (
    <MakemainPositioner>
      <Sidebar />
      <Outputform> 안녕 </Outputform>

      <Inputform>
        <Cololselector>
          명함 컬러 선택하기
          <Colorbuttons>
            <button name="color" value="red" onClick={handleClick}>
              빨강
            </button>
            <button name="color" value="orange" onClick={handleClick}>
              주황
            </button>
            <button name="color" value="yellow" onClick={handleClick}>
              노랑
            </button>
            <button name="color" value="blue" onClick={handleClick}>
              파랑
            </button>
          </Colorbuttons>
        </Cololselector>
        <Basicinfo>기본 정보 입력하기</Basicinfo>
        <Basicinfoinputs>
          <Basicinfoinputposition>
            <label>이름</label>
            <Infoinput
              type="text"
              name="name"
              value={values.name}
              placeholder="   이름을 입력하세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>메일주소</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   e-mail을 입력하세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
        </Basicinfoinputs>
        <Basicinfo>추가 정보 입력하기</Basicinfo>
        <Basicinfoinputs>
          <Basicinfoinputposition>
            <label>회사명</label>
            <Infoinput
              type="text"
              name="name"
              value={values.name}
              placeholder="   회사명을 입력하세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>직급</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   직급을 입력하세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>휴대폰번호</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   휴대폰번호를 입력하세요(010-XXXX-XXXX)"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>전화번호</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   전화번호를 입력하세요(02-XXX-XXXX)"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>주소</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   주소를 입력하세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
          <Basicinfoinputposition>
            <lable>소개</lable>
            <Infoinput
              type="text"
              name="mail"
              value={values.mail}
              placeholder="   나를 설명할 수 있는 소개글을 작성해보세요"
              onChange={handleChange}
            />
          </Basicinfoinputposition>
        </Basicinfoinputs>
      </Inputform>
    </MakemainPositioner>
  );
}

export default Myspace;
