import React, { useState } from "react";
import styled from "styled-components";
import Sidebar from "../Components/Sidebar";

//명함 만들기 main 화면
const MakemainPositioner = styled.div`
  display: flex;
`;
// 출력폼
const Outputform = styled.div`
  border-style: solid;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
`;

// 입력폼
const Inputform = styled.div`
  border-style: solid;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
`;
// 명함 컬러 선택하기
const Cololselector = styled.div`
  display: flex;
  justify-content: center;
  font-size: 20px;
  flex-wrap: wrap;
`;

const Colorbuttons = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
`;

// 기본 정보 입력하기
const Basicinfo = styled.div`
  display: flex;
  border-style: solid;
  height: 50px;
  justify-content: center;
  font-size: 20px;
  margin-top: 1rem;
  border-style: solid;
  flex-wrap: wrap;
`;

const Basicinfoinputs = styled.div`
  display: flex;
  justify-content: center;
`;

const Basicinput = styled.input`
  width: 250px;
`;

function Myspace() {
  const [color, setColor] = useState("");
  const [name, setName] = useState("");
  const [mail, setMail] = useState("");
  console.log(color, "색상");
  console.log(name, "이름");
  return (
    <MakemainPositioner>
      <Sidebar />
      <Outputform> 안녕 </Outputform>

      <Inputform>
        <Cololselector>
          명함 컬러 선택하기
          <Colorbuttons>
            <button onClick={() => setColor("red")}>빨강</button>
            <button onClick={() => setColor("orange")}>주황</button>
            <button onClick={() => setColor("yellow")}>노랑</button>
            <button onClick={() => setColor("blue")}>파랑</button>
          </Colorbuttons>
        </Cololselector>
        <Basicinfo>기본 정보 입력하기</Basicinfo>
        <Basicinfoinputs>
          이름
          <Basicinput
            type="text"
            name="name"
            value={name}
            placeholder="이름을 입력하세요"
            onChange={({ target: { value } }) => setName(value)}
          />
          메일주소
          <Basicinput
            type="text"
            name="mail"
            value={mail}
            placeholder="e-mail을 입력하세요"
            onChange={({ target: { value } }) => setMail(value)}
          />
        </Basicinfoinputs>
      </Inputform>
    </MakemainPositioner>
  );
}

export default Myspace;
