import React, { useState } from "react";
import styled from "styled-components";
import Sidebar from "../Components/Sidebar";
import Axios from "axios";

//명함 만들기 main 화면
const MakemainPositioner = styled.div`
  display: flex;
`;
// 출력폼
const Outputform = styled.div`
  display: flex;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
  justify-content: center;
`;

const Outputlayer = styled.div`
  display: flex;
  width: 90%;
  height: 90%;
  border-style: solid;
  border-radius: 20px;
  font-family: "Noto Sans KR", sans-serif;
  align-items: center;
  flex-direction: column;
`;

// 입력폼
const Inputform = styled.div`
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
`;
// 명함 컬러 선택하기
const Cololselector = styled.div`
  display: flex;
  justify-content: center;
  font-size: 20px;
  flex-wrap: wrap;
`;

const Colorbuttons = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
`;

// 기본 정보 입력하기
const Infotitle = styled.div`
  display: flex;
  height: 40px;
  justify-content: center;
  font-size: 20px;
  margin-top: 1rem;
  flex-wrap: wrap;
`;

const Infoinputs = styled.div`
  display: flex;
  align-items: flex-end;
  flex-direction: column;
`;

const Infoinputposition = styled.div``;

const Infoinput = styled.input`
  width: 500px;
  font-size: 15px;
  margin-top: 0.5rem;
  margin-left: 2rem;
  margin-right: 8rem;
  border-radius: 15px;
`;

const Introduceinput = styled.textarea`
  width: 500px;
  height: 230px;
  font-size: 15px;
  margin-top: 1rem;
  margin-left: 2rem;
  margin-right: 8rem;
  border-radius: 15px;
`;

function Repository() {
  return (
    <MakemainPositioner>
      <Sidebar />
      <Outputform>
        <Outputlayer></Outputlayer>
      </Outputform>
      <Inputform></Inputform>
    </MakemainPositioner>
  );
}

export default Repository;
