import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Sidebar from "../Components/Sidebar";
import Axios from "axios";
import axios from "axios";

//명함 만들기 main 화면
const MakemainPositioner = styled.div`
  display: flex;
  border-style: solid;
`;
// 출력폼
const Outputform = styled.div`
  display: flex;
  width: 50%;
  height: 100vh;
  margin-top: 1rem;
  justify-content: center;
`;

const Outputlayer = styled.div`
  display: flex;
  width: 90%;
  height: 90%;
  border-style: solid;
  border-radius: 20px;
  font-family: "Noto Sans KR", sans-serif;
  align-items: center;
  flex-direction: column;
`;

// 입력폼
const Inputform = styled.div`
  width: 50%;
  height: 100vh;
  border-style: solid;
  margin-top: 1rem;
`;
// http://localhost:3001/api/get

function Repository() {
  const [data, setData] = useState([]);

  useEffect(() => {
    let completed = false; //초기에는 실행해야 되기때문에 false flag 변수

    //query를 리턴하는 함수를 result에 할당
    async function get() {
      const result = await axios.get("http://localhost:3001/api/get");
      if (!completed) setData(result.data);
    }
    get();
    return () => {
      completed = true;
    };
    //query가 변할때 useEffect를 실행해야하는 시점이다.
  }, []);

  return (
    <MakemainPositioner>
      <Sidebar />
      <Outputform>
        <Outputlayer>
          {data.map((item) => (
            <>
              <li key={item.color}>{item.color}</li>
              <li key={item.color}>{item.name}</li>
              <li key={item.color}>{item.mail}</li>
              <li key={item.color}>{item.corporate}</li>
              <li key={item.color}>{item.position}</li>
              <li key={item.color}>{item.phonenumber}</li>
              <li key={item.color}>{item.officenumber}</li>
              <li key={item.color}>{item.address}</li>
              <li key={item.color}>{item.introduce}</li>
              <img src={item.img}></img>
            </>
          ))}
        </Outputlayer>
      </Outputform>
      <Inputform></Inputform>
    </MakemainPositioner>
  );
}

export default Repository;
